import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as n,c as d,b as i,d as e,a as t,e as h}from"./app-8c5ce49e.js";const o={},c=i("h1",{id:"linkage-介绍",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#linkage-介绍","aria-hidden":"true"},"#"),e(" Linkage 介绍")],-1),s={href:"http://www.linkagesimulator.com/",target:"_blank",rel:"noopener noreferrer"},u=h('<h2 id="基本操作" tabindex="-1"><a class="header-anchor" href="#基本操作" aria-hidden="true">#</a> 基本操作</h2><h3 id="基本操作-1" tabindex="-1"><a class="header-anchor" href="#基本操作-1" aria-hidden="true">#</a> 基本操作</h3><ol><li>右键放置构建</li><li><kbd>Ctrl</kbd> 选择多个构建</li><li>Simulate -&gt; Run 开始仿真</li><li>Simulate -&gt; 箭头 手动控制仿真</li></ol><h3 id="机械约束" tabindex="-1"><a class="header-anchor" href="#机械约束" aria-hidden="true">#</a> 机械约束</h3><p>通过 <kbd>Ctrl</kbd> 选中两个元件, 通过 Modify 添加约束</p><ol><li>Joint 铰链约束 以两个点为目标</li><li>Slide 滑块约束 以一个铰链与一根杆为目标</li><li>Link 连接 在两个节点之间创建杆</li><li>Fasten 固定约束 以一个铰链与一个凸轮为目标, 用于确定凸轮的转动中心</li><li>Combine 连接约束 用于制作结构或固连杆与齿轮</li></ol><h3 id="尺寸约束" tabindex="-1"><a class="header-anchor" href="#尺寸约束" aria-hidden="true">#</a> 尺寸约束</h3><p>选中一个或 <kbd>Ctrl</kbd> 多个元件, 通过 Align 添加约束 通常第一个被选中的点的位置不会改变</p><ol><li>Length 长度 可以设定杆长或者两个节点之间的长度</li><li>Angle 角度 通过逆时针选择三个点, 可以指定点的长度</li><li>Horizon / Vertical 对齐</li></ol><h2 id="构件设置" tabindex="-1"><a class="header-anchor" href="#构件设置" aria-hidden="true">#</a> 构件设置</h2><h3 id="主动件运动" tabindex="-1"><a class="header-anchor" href="#主动件运动" aria-hidden="true">#</a> 主动件运动</h3><p>通过固定铰链驱动连接杆的转动</p><ol><li>双击固定铰链约束进行设置</li><li>选择 Rotating input 设置为主动件并确定转速</li><li>通过 Oscillation Limit Angle 设置摆动范围</li></ol><h3 id="绘制运动轨迹" tabindex="-1"><a class="header-anchor" href="#绘制运动轨迹" aria-hidden="true">#</a> 绘制运动轨迹</h3><ol><li>选择要绘制的节点</li><li>勾选 Draw Motion Path</li></ol><h2 id="典型机构" tabindex="-1"><a class="header-anchor" href="#典型机构" aria-hidden="true">#</a> 典型机构</h2><h3 id="机构技巧" tabindex="-1"><a class="header-anchor" href="#机构技巧" aria-hidden="true">#</a> 机构技巧</h3><ol><li>一个杆上有两个固定套筒, 实现移动杆</li><li>使用一个杆与两个固定铰链, 实现滑块</li><li>没有尺寸约束, 可通过重复设置尺寸, 实现特定尺寸约束</li></ol><h3 id="齿轮机构" tabindex="-1"><a class="header-anchor" href="#齿轮机构" aria-hidden="true">#</a> 齿轮机构</h3><ol><li>右键创建两个齿轮</li><li>两个齿轮的中点需要有固定铰链或其中一个有固定铰链, 并与另一齿轮以杆连接 (行星齿轮)</li><li><kbd>Ctrl</kbd> 选中两个齿轮, 在 Align -&gt; Ratio 创建齿轮副</li><li>上方单选框确定类型</li><li>下方确定齿轮的传动比</li><li>对于同轴的齿轮, 要使其能同轴传动, 还需要对同轴的齿轮采用 1:1 传动比的 ratio</li></ol><h3 id="凸轮机构" tabindex="-1"><a class="header-anchor" href="#凸轮机构" aria-hidden="true">#</a> 凸轮机构</h3><ol><li>右键最右下角创建样条曲线</li><li>右键线条 Add node 创建控制点</li><li>右键线条 Close 使线条封闭</li><li>拖动线条节点左右的三角形, 改变控制点的斜率</li><li>创建节点, 选择节点与封闭线条, 使用 Slide 约束, 创建凸轮</li><li>创建移动杆机构 (见上), 将杆的一端与凸轮上的滑块连接, 创建从动件</li></ol>',22);function b(f,k){const a=r("ExternalLinkIcon");return n(),d("div",null,[c,i("blockquote",null,[i("p",null,[i("a",s,[e("官方网站"),t(a)])])]),u])}const p=l(o,[["render",b],["__file","linkage.html.vue"]]);export{p as default};
