import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as l,c as n,b as e,d as a,a as r,e as c}from"./app-8c5ce49e.js";const d={},o=e("h1",{id:"simspace",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#simspace","aria-hidden":"true"},"#"),a(" simspace")],-1),h=e("h2",{id:"基础使用",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#基础使用","aria-hidden":"true"},"#"),a(" 基础使用")],-1),p={href:"https://ww2.mathworks.cn/help/simscape/gs/essential-steps-for-constructing-a-physical-model.html",target:"_blank",rel:"noopener noreferrer"},m=c('<p>以运动模型的建模为例</p><h3 id="创建模型" tabindex="-1"><a class="header-anchor" href="#创建模型" aria-hidden="true">#</a> 创建模型</h3><p>在 Matlab 中使用命令 <code>ssc_new</code> 创建新的 Simspace 模型，使用该方法创建的模型求解器更适合 Simsapce 的求解要求</p><p>该命令还可接收参数 <code>ssc_new(modelname, domain)</code></p><ul><li><code>modelname</code> 字符串，模型名称</li><li><code>domain</code> 字符串，明确模型具体类型，常用的有 <ul><li><code>translational</code> 直线运动模型</li><li><code>rotational</code> 转动运动模型</li><li><code>electrical</code> 电路系统模型</li><li><code>isothermal_liquid</code> 等温液压模型</li><li><code>gas</code> 气动模型</li></ul></li></ul><p>任何 Simsapce 模型中必须包含模块 Solver Configuration 配置求解器</p><ul><li>该模块位于 Simspace/Utility 中</li><li>通过配置改模块的属性，可修改 Simspace 的求解设置</li><li>该模块一般与模型中的参考点连接</li></ul><h3 id="物理网络" tabindex="-1"><a class="header-anchor" href="#物理网络" aria-hidden="true">#</a> 物理网络</h3><p>通过 Simscape/Foundation Library/&lt;模型类别&gt;/XXX Elements 中的模块对应的模型中的部件</p><p>通过部件模块的属性，可设置部件的物理参数与初始条件，根据初始条件的优先级，求解器将尽量使初值满足初始条件</p><p>物理网络中允许串联与并联，并联相当于将多个部件连接与部件的同一侧</p><p>构建物理网络时，必须注意模型的参考点</p><ul><li>参考点一般为部件，不同的类型的模型参考点不同，有模块名 XXX Referen</li><li>参考点相当于一个状态量及其导数均为 0 的点</li><li>多个网络交互时，每个独立的网络至少要有一个参考点</li><li>允许一个网络中有多个参考点模块</li></ul><h3 id="添加信号源" tabindex="-1"><a class="header-anchor" href="#添加信号源" aria-hidden="true">#</a> 添加信号源</h3><p>通过 Simscape/Foundation Library/&lt;模型类别&gt;/XXX Sources 中的模块对应的模型中的部件</p><p>信号源具有方向性，信号源的 R 端为输入，C 端为输出，当 C 端与两个部件连接时，表明为两个部件间的相对状态参数以指定的规律运动<br> 如果希望表示外加的力，电压等，应当将 C 端与参考点相连</p><p>信号源通过 S 端接收 PS 信号确定的大小，一般</p><h3 id="添加传感器" tabindex="-1"><a class="header-anchor" href="#添加传感器" aria-hidden="true">#</a> 添加传感器</h3><h3 id="simulink-交互" tabindex="-1"><a class="header-anchor" href="#simulink-交互" aria-hidden="true">#</a> Simulink 交互</h3><h3 id="仿真执行与结果" tabindex="-1"><a class="header-anchor" href="#仿真执行与结果" aria-hidden="true">#</a> 仿真执行与结果</h3><h2 id="运动模型" tabindex="-1"><a class="header-anchor" href="#运动模型" aria-hidden="true">#</a> 运动模型</h2><h2 id="机械系统模型" tabindex="-1"><a class="header-anchor" href="#机械系统模型" aria-hidden="true">#</a> 机械系统模型</h2><p>需要额外安装模块 Simscape Multibody</p>',23),u={href:"https://blog.csdn.net/weixin_45717224/article/details/136260638",target:"_blank",rel:"noopener noreferrer"};function _(f,b){const i=t("ExternalLinkIcon");return l(),n("div",null,[o,h,e("p",null,[a("参考教程 "),e("a",p,[a("https://ww2.mathworks.cn/help/simscape/gs/essential-steps-for-constructing-a-physical-model.html"),r(i)])]),m,e("p",null,[e("a",u,[a("https://blog.csdn.net/weixin_45717224/article/details/136260638"),r(i)])])])}const g=s(d,[["render",_],["__file","simspace.html.vue"]]);export{g as default};
