import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as i,e as h}from"./app-8c5ce49e.js";const r={},d=h('<h2 id="装配图画法" tabindex="-1"><a class="header-anchor" href="#装配图画法" aria-hidden="true">#</a> 装配图画法</h2><ol><li>拆卸画法 可以半拆卸，要写明拆卸零件</li><li>假想画法 假想部分用双点划线</li><li>夸大化法 两条线强调非配合面</li><li>展开画法 依齿轮轴剖切</li></ol><h2 id="尺寸" tabindex="-1"><a class="header-anchor" href="#尺寸" aria-hidden="true">#</a> 尺寸</h2><h3 id="规格尺寸" tabindex="-1"><a class="header-anchor" href="#规格尺寸" aria-hidden="true">#</a> 规格尺寸</h3><p>体现装配体性能，如阀口直径体现阀的流量大小</p><h3 id="装配尺寸" tabindex="-1"><a class="header-anchor" href="#装配尺寸" aria-hidden="true">#</a> 装配尺寸</h3><p>配合尺寸与轴线位置等有公差的尺寸</p><h3 id="安装尺寸" tabindex="-1"><a class="header-anchor" href="#安装尺寸" aria-hidden="true">#</a> 安装尺寸</h3><p>安装孔的大小位置</p><h3 id="外形尺寸" tabindex="-1"><a class="header-anchor" href="#外形尺寸" aria-hidden="true">#</a> 外形尺寸</h3><p>总长总宽总高</p><h3 id="技术要求" tabindex="-1"><a class="header-anchor" href="#技术要求" aria-hidden="true">#</a> 技术要求</h3><p>只使用文字</p><h2 id="明细栏" tabindex="-1"><a class="header-anchor" href="#明细栏" aria-hidden="true">#</a> 明细栏</h2><ol><li>自下而上，自小到大</li><li>可以多栏，最左侧粗实线</li></ol><h2 id="合理装配结构" tabindex="-1"><a class="header-anchor" href="#合理装配结构" aria-hidden="true">#</a> 合理装配结构</h2><ol><li>两零件接触，在同一个方向只能有一个接触面 (圆锥销斜面与底面为一个方向，斜面配合就不能要求底面与零件表面平齐)</li><li>孔轴结构中，要有倒角/切槽便于插入</li><li>轴肩小于轴承内圈高度，孔肩小于轴承外圈高度，便于拆卸</li><li>零件之间留出空间，考虑安装方便</li><li>压盖螺母不能压紧为一条线</li><li>其中一侧的轴端面与孔端面配合时，另一侧轴端面略小于孔端面，轴肩与轴面不接触</li></ol>',17),l=[d];function n(t,c){return e(),i("div",null,l)}const _=a(r,[["render",n],["__file","装配图.html.vue"]]);export{_ as default};
